div.script:
function htmlDecode(input)
    {
       var e = document.createElement('div');
       e.innerHTML = input;
       return e.childNodes[0].nodeValue;



function copyToClipboard(element) 
    {
       var $temp = $("");
       $("body").append($temp);
       var a = $(element).html().trim().replace(/<br>/g, '\n').replace(/<\/?[^>]+>/g, '');
       var x = htmlDecode(a);
       $temp.val(x).select();
       document.execCommand("copy");
       $temp.remove();
    }
